con:config.script:
def roleID = testRunner.testCase.getPropertyValue("Role - Admin ID")
def userID = testRunner.testCase.getPropertyValue("User Role ID")

log.info userID

def myString = roleID.replace("[","")
myString = myString.replace("]","")

def secondString = userID.replace("[","")
finalString = secondString.replace("]","")

log.info myString
log.info finalString

testRunner.testCase.setPropertyValue("Role - Admin ID",myString)
testRunner.testCase.setPropertyValue("User Role ID",finalString)

