import org.apache.commons.lang.RandomStringUtils 
charset = (('a'..'z') + ('A'..'Z') + ('0'..'9')).join()
length1 = 5
length2 = 4

testRunner.testCase.setPropertyValue("App Name","App Test - "+RandomStringUtils.random(length1, charset.toCharArray()));
testRunner.testCase.setPropertyValue("DF Name","Data Form "+RandomStringUtils.random(length1, charset.toCharArray()));
testRunner.testCase.setPropertyValue("Page Name","Page Name"+RandomStringUtils.random(length1, charset.toCharArray()));
testRunner.testCase.setPropertyValue("Navigation Page Name","Nav Name"+RandomStringUtils.random(length1, charset.toCharArray()));

